Vue.component('test', {
    data: function() {
        return {
            phh: 7,
            fragg: 15,
            chartInstancee: "",
            seqs: ",,,,,,,,"
        }
    },
    methods: {
        fetchDataa(){
            return new Promise((resolve, reject) => {
                urlStr = "/api?ph="+this.phh.toString()+"&seqs="+this.seqs+"&fraglen="+this.fragg.toString()
                fetch(urlStr)
                    .then((res) => {
                        resolve(res.json())
                    });
            });

        },
        getDataObjj(apiRes){
            var dataParams = {
                labels: apiRes.labels,
                datasets: [{
                    label: apiRes.species,
                    data: apiRes.val,
                    fill: false,
                    borderColor: 'rgb(75, 192, 192)',
                    tension: 0
                }]
            };
            return dataParams
        },
        newChartt(dataParams, title){
            const ctx = document.getElementById("testGraph").getContext("2d");
            var options = {
                maintainAspectRatio: false,
                plugins: {
                    title: {
                        display: true,
                        text: title
                    }
                },
                scales: {
                    x: {
                        beginAtZero: true
                    },
                    y: {
                        beginAtZero: false
                    }
                }
            }
            const chart = new Chart(ctx, {
                type: "line",
                data: dataParams,
                options: options
            });
            this.chartInstancee = chart;
        },
        refreshChartt(){
            selff = this;
            this.fetchDataa()
            .then((data) => {
                apiRes = data;
                var dataParams = selff.getDataObjj(apiRes);
                selff.chartInstancee.destroy();
                selff.newChartt(dataParams, apiRes.title);
            });
        }
    },
    watch:{
        phh(newPH){
            this.phh = newPH;
            this.refreshChartt();
        },
        fragg(newLen){
            this.fragg = newLen;
            this.refreshChartt();
        }
    },
    mounted(){
        this.$nextTick(() => {
            selff = this;
            selff.fetchDataa()
            .then((data) => {
                apiRes = data;
                var dataParams = selff.getDataObjj(apiRes);
                selff.newChartt(dataParams, apiRes.title);
            });
        });
    },
    template: '<div><label for="a" class="form-label">Fragment Length</label><input id="a" type="range" min=1 max=30 class="form-range" v-model="fragg" /><label for="b" class="form-label">pH</label><input id="b" type="range" min=0 max=14 class="form-range" v-model="phh" /></div>'
});
        
